 to cloning\r\n\r\n    // Clone items to make seamless looping\r\n    for (let i = 0; i < itemsPerView; i++) {\r\n        const cloneStart = items[i].cloneNode(true); // Clone first few items\r\n        const cloneEnd = items[totalItems - 1 - i].cloneNode(true); // Clone last few items\r\n        carousel.appendChild(cloneStart); // Append clones to the end\r\n        carousel.insertBefore(cloneEnd, carousel.firstChild); // Prepend clones to the start\r\n    }\r\n\r\n    // Re-calculate items after cloning\r\n    items = carousel.children;\r\n    const totalClonedItems = items.length;\r\n\r\n    // Set initial offset for the carousel\r\n    const itemWidth = 100 / itemsPerView;\r\n    carousel.style.transform = `translateX(-${currentIndex * itemWidth}%)`;\r\n\r\n    function updateCarousel() {\r\n        const offset = -currentIndex * itemWidth;\r\n        carousel.style.transition = \'transform 0.3s ease-in-out\';\r\n        carousel.style.transform = `translateX(${offset}%)`;\r\n\r\n        // Reset position for seamless looping\r\n        carousel.addEventListener(\'transitionend\', () => {\r\n            if (currentIndex < itemsPerView) {\r\n                currentIndex = totalItems + currentIndex; // Move to the end clones\r\n                carousel.style.transition = \'none\';\r\n                carousel.style.transform = `translateX(-${currentIndex * itemWidth}%)`;\r\n            }\r\n            if (currentIndex >= totalItems + itemsPerView) {\r\n                currentIndex = currentIndex - totalItems; // Move to the beginning clones\r\n                carousel.style.transition = \'none\';\r\n                carousel.style.transform = `translateX(-${currentIndex * itemWidth}%)`;\r\n            }\r\n        }, { once: true });\r\n    }\r\n\r\n    prev.addEventListener(\'click\', () => {\r\n        currentIndex--;\r\n        updateCarousel();\r\n    });\r\n\r\n    next.addEventListener(\'click\', () => {\r\n        currentIndex++;\r\n        updateCarousel();\r\n    });\r\n});\r\n\r\n        // carousal one end\r\n        // tab js\r\n        const tabs = document.querySelectorAll(\'.tab-option\');\r\n        const tabcontent = document.querySelectorAll(\'.tab-content\');\r\n\r\n        tabs.forEach(tab => {\r\n            tab.addEventListener(\'click\', () => {\r\n                // Remove active class from all tabs and contents\r\n                tabs.forEach(t => t.classList.remove(\'active\'));\r\n                tabcontent.forEach(c => c.classList.remove(\'active\'));\r\n\r\n                // Add active class to clicked tab and corresponding content\r\n                tab.classList.add(\'active\');\r\n                document.getElementById(tab.dataset.tab).classList.add(\'active\');\r\n            });\r\n        });\r\n</script>\";s:27:\"divi_integration_single_top\";s:0:\"\";s:30:\"divi_integration_single_bottom\";s:0:\"\";s:15:\"divi_468_enable\";s:5:\"false\";s:14:\"divi_468_image\";s:0:\"\";s:12:\"divi_468_url\";s:0:\"\";s:16:\"divi_468_adsense\";s:0:\"\";s:12:\"divi_favicon\";s:0:\"\";}',	'on'),
